import React from "react";
import classes from "./Meal.module.css";

function Meal(props) {
  return (
    <li className={classes.meal}>
      <span className={classes.head}>{props.meal.name}</span>
      <span className={classes.description}>{props.meal.description}</span>
      <span className={classes.price}>
        {new Intl.NumberFormat("en-NG", { style: "currency" , currency:'ng-NGNS'}).format(
          props.meal.price
        )}
      </span>
    </li>
  );
}

export default Meal;
