import React, { useContext } from "react";
import classes from "./HeaderCartButton.module.css";
import CartIcon from "../Cart/CartIcon";
import CartContext from "../../store/cart-context";

const HeaderCartButton = (props) => {
  const cartContext = useContext(CartContext);
  const numberOfCartItems = cartContext.items.reduce((cNumber, item) => {
    return cNumber + item.amount;
  }, 0);

  const btnClasses = `${classes}`;
  return (
    <button className={classes.button} onClick={props.onCartButtonClick}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{numberOfCartItems}</span>
      {/* <CartContext.Consumer>
        {(ctx) => <span className={classes.badge}>{ctx.items.length}</span>}
      </CartContext.Consumer> */}
    </button>
  );
};

export default HeaderCartButton;
