import React, { useContext } from "react";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import CartContext from "../../store/cart-context";

const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  // const totalAmount = new Intl.NumberFormat("en-NG", {
  //   style: "currency",
  //   currency: "NGN",
  // }).format(
  //   cartCtx.items.reduce((cItem, item) => {
  //     return cItem + item.price * item.amount;
  //   }, 0)
  // );
  const totalAmount = cartCtx;
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <li key={item.id}>{item.name}</li>
      ))}
    </ul>
  );
  const onCartCloseClick = () => {
    props.onCartCloseClick();
  };
  return (
    <Modal onOverlayClick={props.onCartCloseClick}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={onCartCloseClick}>
          Close
        </button>
        <button
          className={classes.button}
          onClick={() => console.log("Ordering...")}
        >
          Order
        </button>
      </div>
    </Modal>
  );
};

export default Cart;
